 import java.awt.*;
 import java.awt.event.*;
 import javax.swing.*;

 public class Linky
     extends JFrame
     implements ActionListener {

     private int WindowWidth = 800;
     private int WindowHeight = 600;
     private String AppTitle = "Linky - Text-Browser";
     private int _WindowXPosition = 100;
     private int _WindowYPosition = 100;

     private BorderLayout layout;

     public App(String appTitle,
                int windowXPosition,
                int windowYPosition,
                int windowWidth,
                int windowHeight)
     {
         super(appTitle);
         layout = new BorderLayout( windowXPosition,
                                    WindowYPosition);
         Container c = getContentPane();
         c.setLayout( layout );
         setSize( windowWidth, windowHeight );
         show();
     }

     public void actionPerformed( ActionEvent e )
     {
     }

     public static void main( String args[] )
     {
         Linky linkyApp = new Linky();
         linkyApp.addWindowListener(new WindowAdapter() {
                 public void windowClosing( WindowEvent e )
                 {
                     System.exit( 0 );
                 }
             });
     }
 }
